import './index.css';
import React, {useState} from 'react'
import {BrowserRouter, NavLink, Router, Route, Routes} from "react-router-dom"
import Showcase from './Showcase'
import Search from './Search'
import Home from './Home'
//import {css} from 'glamor'

// const style = { 
//   MenuLink: {
//     textDecoration: 'none',
//     color: 'black',
//     ':hover': {
//       color: 'indigo',
//       textShadow: '`indigo -2px -2px`'
//     }
//   }
// }

function App() {

//const [color, setColor] = useState('black')
//const [textShadow, setTextShadow] = useState('')
const [hovered, setHovered] = useState('')

function handleHoverOver(event) {
//  // setTextShadow(`indigo -2px -2px`)
//  // setColor('magenta')
 setHovered('hoveredLink')

 } 

 function handleHoverOut() {
//  // setTextShadow(``)
//  // setColor('black')
  setHovered('')
 }

  return (
    <BrowserRouter>
    <div className="App">

      <div className="buttonsContainer">
          <NavLink exact activeclassname="active" to="/search"
            //activeStyle={{color: 'green'}}
            //style={...css(style.MenuLink)}
            style={{
              //color: `${color}`,
              textDecoration: 'none'
              //textShadow: `${textShadow}`

            }}
            onMouseEnter={handleHoverOver}
            onMouseLeave={handleHoverOut}
            className= {hovered}

          >Search Tweets</NavLink>  
          <NavLink exact activeclassname="active" to="/"
            style={{
              //color: `${color}`,
              textDecoration: 'none'
              //textShadow: `${textShadow}`

            }}
            onMouseEnter={handleHoverOver}
            onMouseLeave={handleHoverOut}
            className= {hovered}
          >Home</NavLink>  
          <NavLink exact activeclassname="active" to="/showcase"
            style={{
              //color: `${color}`,
              textDecoration: 'none'
              //textShadow: `${textShadow}`
            }}
            onMouseEnter={handleHoverOver}
            onMouseLeave={handleHoverOut}
            className= {hovered}
          >Go to Showcase</NavLink>  
      </div>
        <Routes>
            <Route path="/search" element={<Search />} />
            <Route path="/showcase" element={<Showcase />} />
            <Route path="/" element={<Home />} />
        </Routes>

    </div>
    </BrowserRouter>
  );
}

export default App;
