import './index.css';
import {BrowserRouter, Link, Router, Route, Routes, useState} from "react-router-dom"
import Showcase from './Showcase'
import Search from './Search'
import Home from './Home'



function App() {

  const [color, setColor] = useState('black')

function handleHover() {
  if (color === 'black') {
    setColor('hotpink')
  } else {
    setColor('black')
  }
}
  return (
    <BrowserRouter>
    <div className="App">

      <div className="buttonsContainer">
        <Link to="/search"
          style={{
            color: `${color}`,
            textDecoration: 'none'
          }}
        >Search Tweets</Link>  
        <Link to="/"
          style={{
            color: `${color}`,
            textDecoration: 'none'
          }}
        >Home</Link>  
        <Link to="/showcase"
          style={{
            color: `${color}`,
            textDecoration: 'none'
          }}
        >Go to Showcase</Link>  

        <Routes>
            <Route path="/search" element={<Search />} />
            <Route path="/showcase" element={<Showcase />} />
            <Route path="/" element={<Home />} />
        </Routes>
      </div>
    </div>
    </BrowserRouter>
  );
}

export default App;
