import './index.css';
import React, {useState} from 'react'
import {BrowserRouter, NavLink, Route, Routes} from "react-router-dom"
import Showcase from './Showcase'
import Search from './Search'
import Home from './Home'

function App() {

const [hovered1, setHovered1] = useState('')
const [hovered2, setHovered2] = useState('')
const [hovered3, setHovered3] = useState('')

function handleHoverOver1() {
  setHovered1('hoveredLink')
 } 

 function handleHoverOut1() {
    setHovered1('')
 }

 function handleHoverOver2() {
  setHovered2('hoveredLink')
 } 

 function handleHoverOut2() {
    setHovered2('')
 }

 function handleHoverOver3() {
  setHovered3('hoveredLink')
 } 

 function handleHoverOut3() {
    setHovered3('')
 }

  return (
    <BrowserRouter>
    <div className="App">

      <div className="buttonsContainer">
          <NavLink exact="true" activeclassname="active" to="/search"
            style={{
              textDecoration: 'none'

            }}
            onMouseEnter={handleHoverOver1}
            onMouseLeave={handleHoverOut1}
            className= {hovered}

          >Search Tweets</NavLink>  
          <NavLink exact="true" activeclassname="active" to="/"
            style={{
              textDecoration: 'none'

            }}
            onMouseEnter={handleHoverOver2}
            onMouseLeave={handleHoverOut2}
            className= {hovered}
          >Home</NavLink>  
          <NavLink exact="true" activeclassname="active" to="/showcase"
            style={{
              textDecoration: 'none'
            }}
            onMouseEnter={handleHoverOver3}
            onMouseLeave={handleHoverOut3}
            className= {hovered}
          >Go to Showcase</NavLink>  
      </div>
        <Routes>
            <Route path="/search" element={<Search />} />
            <Route path="/showcase" element={<Showcase />} />
            <Route path="/" element={<Home />} />
        </Routes>

    </div>
    </BrowserRouter>
  );
}

export default App;
